let hasDocument = false;
try {
  document;
  hasDocument = true;
} catch (error) {}

const constants = {
  documentHead: hasDocument
    ? document.getElementsByTagName("head")[0]
    : undefined,
  apiUrl: "https://api.anymod.com/v2/",
  cdnUrl: "https://cdn.anymod.com/",
};

export default constants;
