const loadScript = (url, cb, opts) => {
  try {
    document;
  } catch (error) {
    return;
  }
  opts = opts || {}; // { id, async, defer }
  let js = document.createElement("script");
  if (opts.hasOwnProperty("id")) js.id = opts.id;
  if (opts.hasOwnProperty("async")) js.setAttribute("async", opts.async);
  if (opts.hasOwnProperty("defer")) js.setAttribute("defer", opts.defer);
  if (cb) js.onload = cb;
  document.body && document.body.appendChild(js);
  js.src = url;
};

export { loadScript };
