function sum(...nums) {
    // node
    let i = 0, total = 0;
    for (; i < nums.length; i++)
        total += nums[i];
    return total;
}
function substract(...nums) {
    let i = 0, total = nums[i++] | 0;
    for (; i < nums.length; i++)
        total -= nums[i];
    return total;
}
function average(...nums) {
    let i = 0, len = nums.length, total = 0;
    for (; i < len; i++)
        total += nums[i];
    return total / len;
}

exports.average = average;
exports.substract = substract;
exports.sum = sum;
